void solve()
{
    long long  n;
    cin>>n;
    long long  arr[n+1];
    DSU d(n);
    for(long long  i=1;i<=n;i++)
    {
        cin>>arr[i];
    }
    vector<pair<long long ,long long >>ans;
    for(long long  i=n-1;i>=1;i--)
    {
        gp_hash_table<long long ,vector<long long >>mp;
        for(long long  j=1;j<=n;j++)
        {
            mp[arr[j]%i].push_back(j);
        }
        long long  flag=0;
        for(auto it:mp)
        {
            if(flag)
            break;
            auto v=it.second;
            long long  sz=(long long )v.size();
            long long xx=d.findUlp(v[0]);
            for(long long  k1=1;k1<sz;k1++)
            {
                if(flag)
                {
                    break;
                }
                long long yy=d.findUlp(v[k1]);
                if(xx==yy)
                continue;
                d.unionBySize(v[0],v[k1]);
                ans.push_back({v[0],v[k1]});
                flag=1;
            }
        }
    }
    // debug(d.cc);
    if(d.cc!=1)
    {
        cout<<"NO"<<endl;
        return;
    }
    cout<<"YES"<<endl;
    reverse(all(ans));
    for(auto it:ans)
    {
        cout<<it.first<<" "<<it.second<<endl;
    }

}